// Initialize workflow variables and validate environment
const requiredVars = [
  'GOOGLE_SHEETS_SPREADSHEET_ID',
  'GOOGLE_SHEETS_TAB_NAME', 
  'OPENAI_API_KEY',
  'SPREAKER_CLIENT_ID',
  'SPREAKER_CLIENT_SECRET',
  'SPREAKER_REFRESH_TOKEN',
  'SPREAKER_SHOW_ID'
];

const missing = requiredVars.filter(v => !$vars[v]);
if (missing.length > 0) {
  throw new Error(`Missing required variables: ${missing.join(', ')}`);
}

// Set default values
const config = {
  MAX_EPISODES: parseInt($vars.MAX_EPISODES_PER_RUN || '2'),
  DRY_RUN: ($vars.DRY_RUN || 'false').toLowerCase() === 'true',
  TIMEZONE: $vars.EPISODE_TIMEZONE || 'UTC',
  PUBLISH_TIME: $vars.SPREAKER_PUBLISH_TIME_UTC || '08:00:00',
  OPENAI_TEXT_MODEL: $vars.OPENAI_TEXT_MODEL || 'gpt-4o'
};

// Log startup
console.log('üöÄ Starting Roo Podcast Automation Workflow');
console.log('Configuration:', config);

return [{
  json: {
    config,
    timestamp: new Date().toISOString(),
    status: 'initialized'
  }
}];

// Filter episodes for processing
const rows = $input.all();
const config = $('Initialize Workflow').first().json.config;
const now = new Date();
const maxDate = new Date(now.getTime() + (180 * 24 * 60 * 60 * 1000)); // 180 days from now
const minDate = new Date(now.getTime() - (365 * 24 * 60 * 60 * 1000)); // 1 year ago

const candidates = [];
let skipped = {
  invalidDate: 0,
  outOfRange: 0,
  alreadyGenerated: 0,
  emptyDate: 0
};

for (const item of rows) {
  const row = item.json;

  // Get publish date from various possible column names
  const pubRaw = row['publish_date'] || row['date'] || row['episode date'] || row['publish'] || '';

  if (!pubRaw || pubRaw.trim() === '') {
    skipped.emptyDate++;
    continue;
  }

  // Parse date in DD/MM/YYYY format
  let publishDate;
  try {
    const parts = pubRaw.split('/');
    if (parts.length === 3) {
      const day = parseInt(parts[0]);
      const month = parseInt(parts[1]) - 1; // Month is 0-indexed
      const year = parseInt(parts[2]);
      publishDate = new Date(year, month, day);

      if (isNaN(publishDate.getTime())) {
        throw new Error('Invalid date');
      }
    } else {
      throw new Error('Date not in DD/MM/YYYY format');
    }
  } catch (error) {
    skipped.invalidDate++;
    continue;
  }

  // Check date range
  if (publishDate < minDate || publishDate > maxDate) {
    skipped.outOfRange++;
    continue;
  }

  // Check if already generated (only skip if explicitly TRUE/YES/1)
  const generated = row['generated'] || '';
  const isExplicitlyGenerated = generated && generated.toString().trim() !== '' && 
    ['true', 'yes', '1', 'TRUE', 'YES'].includes(generated.toString().trim());

  if (isExplicitlyGenerated) {
    skipped.alreadyGenerated++;
    continue;
  }

  // Get topic and type
  const topic = row['topic'] || row['title'] || row['episode topic'] || row['episode title'] || 'Untitled';
  const episodeType = (row['type'] || row['episode_type'] || row['episode type'] || '').toLowerCase();
  const type = episodeType.includes('friday') || episodeType.includes('healing') ? 'friday' : 'main';

  candidates.push({
    ...row,
    _rowIndex: row._rowIndex || candidates.length + 2, // Assume header row
    _parsedDate: publishDate,
    _topic: topic,
    _type: type,
    _inputString: `${publishDate.toLocaleDateString('en-GB')} ${type === 'main' ? 'Main Podcast' : 'Friday Healing'} **${topic}** ${topic}`
  });
}

// Sort by date and limit to MAX_EPISODES
const sortedCandidates = candidates.sort((a, b) => a._parsedDate - b._parsedDate);
const toProcess = sortedCandidates.slice(0, config.MAX_EPISODES);

console.log(`Found ${candidates.length} candidate episodes`);
console.log(`Skipped: ${skipped.invalidDate} invalid dates, ${skipped.outOfRange} out of range, ${skipped.alreadyGenerated} already generated, ${skipped.emptyDate} empty dates`);
console.log(`Will process: ${toProcess.length} episodes`);

if (toProcess.length === 0) {
  return [{ json: { status: 'no_episodes', message: 'No episodes to process', skipped } }];
}

// Return each episode as separate item for parallel processing
return toProcess.map(episode => ({ json: episode }));

// Generate episode content using OpenAI
const episode = $json;
const config = $('Initialize Workflow').first().json.config;

// Episode structures for content generation
const EPISODE_STRUCTURES = {
  main: [
    {name: 'Opening & Welcome', target: 500, description: 'Warm opening with episode preview'},
    {name: 'Topic Introduction', target: 1000, description: 'Personal story and topic setup'},
    {name: 'Deep Dive Part 1', target: 1200, description: 'Core concepts and experiences'},
    {name: 'Research & Evidence', target: 1500, description: 'Studies, citations, and scientific backing'},
    {name: 'Deep Dive Part 2', target: 1200, description: 'Advanced concepts and nuances'},
    {name: 'Listener Stories', target: 1500, description: 'Community experiences and validation'},
    {name: 'Practical Tools Part 1', target: 1000, description: 'Techniques and exercises'},
    {name: 'Practical Tools Part 2', target: 1000, description: 'More tools and real-world application'},
    {name: 'Integration & Wrap-up', target: 600, description: 'Bringing it all together and closing'}
  ],
  friday: [
    {name: 'Opening & Welcome', target: 400, description: 'Warm Friday healing opening'},
    {name: 'Topic Exploration', target: 800, description: 'Core topic with personal stories'},
    {name: 'Research & Evidence', target: 600, description: 'Supporting studies and citations'},
    {name: 'Community Focus', target: 700, description: 'Listener stories and shared experiences'},
    {name: 'Practical Tools', target: 400, description: 'Healing techniques and exercises'},
    {name: 'Closing & Preview', target: 300, description: 'Gentle wrap-up and next episode preview'}
  ]
};

const sections = EPISODE_STRUCTURES[episode._type];

console.log(`Processing episode: ${episode._topic} (${episode._type})`);
console.log(`Row ${episode._rowIndex}: ${episode._inputString}`);

return [{
  json: {
    ...episode,
    sections,
    contentGenerationStage: 'ready',
    currentSection: 0,
    completedSections: [],
    combinedScript: '',
    generatedContent: {
      script: '',
      title: '',
      description: '',
      htmlDescription: '',
      tags: ''
    }
  }
}];

// Create section prompt for OpenAI
const episode = $json;
const section = episode.sections[episode.currentSection];
const input = episode._inputString;
const completedSections = episode.completedSections;
const index = episode.currentSection;
const totalSections = episode.sections.length;

function createSectionPrompt(section, input, completedSections, index, totalSections) {
  const prompt = `You are Gregory, host of "CPTSD: Let's Make Sense of This Shit." Generate the "${section.name}" section that flows seamlessly from previous sections.

EPISODE INPUT: ${input}

SECTION REQUIREMENTS:
- Section: ${section.name}
- Target: EXACTLY ${section.target} words (count as you write)
- Purpose: ${section.description}
- Voice: Gregory's compassionate, validating, gently humorous, deeply practical style
- Format: Natural conversational paragraphs only - no headings, lists, or stage directions

${index === 0 ? 'OPENING SECTION - Start with a warm welcome (avoid clich√©s). Include a detailed preview.' : 'CONTINUATION SECTION - Flow from prior content without re-introductions.'}
${index === totalSections - 1 ? 'CLOSING SECTION - Wrap up naturally. End with the Supporters Club CTA exactly as specified.' : ''}

${completedSections.length > 0 ? `Previous sections covered: ${completedSections.map(s => s.name).join(', ')}
Last section ended with: ${completedSections[completedSections.length - 1].content.split(' ').slice(-50).join(' ')}` : 'This is the first section.'}

CRITICAL FLOW RULES:
- No repeated intros
- Use natural transitions
- EXACTLY ${section.target} words
- Continue a single uninterrupted conversation.`;

  return prompt;
}

const prompt = createSectionPrompt(section, input, completedSections, index, totalSections);

return [{
  json: {
    ...episode,
    sectionPrompt: prompt,
    currentSectionData: section
  }
}];

// Process generated section and check if more sections needed
const episode = $json;
const generatedContent = $('Generate Section').first().json.message.content;

// Word count function
function wordCount(text) {
  return text.trim().split(/\s+/).filter(word => word.length > 0).length;
}

const wc = wordCount(generatedContent);
const completedSection = {
  name: episode.currentSectionData.name,
  content: generatedContent,
  wordCount: wc
};

// Update episode data
const updatedCompletedSections = [...episode.completedSections, completedSection];
const updatedCombinedScript = episode.combinedScript + 
  (episode.currentSection > 0 ? '\n\n' : '') + generatedContent;

const nextSectionIndex = episode.currentSection + 1;
const hasMoreSections = nextSectionIndex < episode.sections.length;

console.log(`Section "${episode.currentSectionData.name}" completed: ${wc} words`);

if (hasMoreSections) {
  // More sections to generate
  return [{
    json: {
      ...episode,
      currentSection: nextSectionIndex,
      completedSections: updatedCompletedSections,
      combinedScript: updatedCombinedScript,
      contentGenerationStage: 'continue'
    }
  }];
} else {
  // All sections complete, move to final content generation
  return [{
    json: {
      ...episode,
      completedSections: updatedCompletedSections,
      combinedScript: updatedCombinedScript,
      contentGenerationStage: 'script_complete'
    }
  }];
}

// Combine all generated content and add Supporters Club CTA
const episode = $('Process Section').first().json;
const title = $('Generate Title').first().json.message.content.trim().replace(/^"|"$/g, '');
const description = $('Generate Description').first().json.message.content;
const htmlDescription = $('Generate HTML Description').first().json.message.content;
const rawTags = $('Generate Tags').first().json.message.content;

// Sanitize tags
function sanitizeTags(tags) {
  return tags
    .split(',')
    .map(tag => tag.trim().toLowerCase().replace(/[^a-z0-9]/g, ''))
    .filter(tag => tag.length > 0)
    .slice(0, 20)
    .join(',');
}

const tags = sanitizeTags(rawTags);

// Add Supporters Club CTA if not already present
let finalScript = episode.combinedScript;
if (!finalScript.toLowerCase().includes('supporters club')) {
  finalScript += "\n\nIf this podcast helps you make sense of your shit, join our Supporters Club to go ad-free and help us keep these conversations going. You can find the link to join in the episode description.";
}

const generatedContent = {
  script: finalScript,
  title,
  description,
  htmlDescription,
  tags
};

console.log(`Content generation complete for episode: ${episode._topic}`);
console.log(`Title: ${title}`);
console.log(`Script length: ${finalScript.length} characters`);
console.log(`Tags: ${tags}`);

return [{
  json: {
    ...episode,
    generatedContent,
    contentGenerationStage: 'complete'
  }
}];

// Check if we're in dry run mode
const config = $('Initialize Workflow').first().json.config;
const episode = $json;

if (config.DRY_RUN) {
  console.log(`DRY RUN: Would upload episode: ${episode.generatedContent.title}`);
  console.log(`Script: ${episode.generatedContent.script.substring(0, 200)}...`);
  console.log(`Audio size: ${$('Generate Audio').first().binary.data.length} bytes`);

  return [{
    json: {
      ...episode,
      uploadResult: {
        success: true,
        dryRun: true,
        episodeId: 'dry-run-12345',
        episodeUrl: 'https://spreaker.com/dry-run-episode'
      }
    }
  }];
}

// Prepare for actual upload
return [{
  json: {
    ...episode,
    readyForUpload: true
  }
}];

// Process upload result and prepare for Google Sheets update
const episode = $('Check Dry Run').first().json;
const uploadResponse = $('Upload to Spreaker').first().json;

let uploadResult;
if (episode.uploadResult?.dryRun) {
  // Dry run result
  uploadResult = episode.uploadResult;
} else {
  // Actual upload result
  uploadResult = {
    success: true,
    episodeId: uploadResponse.response?.episode?.episode_id || uploadResponse.episode_id,
    episodeUrl: uploadResponse.response?.episode?.playback_url || uploadResponse.playback_url || `https://spreaker.com/episode/${uploadResponse.response?.episode?.episode_id || uploadResponse.episode_id}`
  };
}

console.log(`Upload ${uploadResult.success ? 'successful' : 'failed'} for episode: ${episode.generatedContent.title}`);
if (uploadResult.episodeId) {
  console.log(`Episode ID: ${uploadResult.episodeId}`);
  console.log(`Episode URL: ${uploadResult.episodeUrl}`);
}

return [{
  json: {
    ...episode,
    uploadResult,
    readyForSheetsUpdate: true
  }
}];

// Final workflow completion
const processedEpisodes = $input.all().map(item => ({
  title: item.json.generatedContent?.title || 'Unknown',
  topic: item.json._topic,
  type: item.json._type,
  row: item.json._rowIndex,
  status: item.json.uploadResult?.success ? 'Success' : 'Failed',
  episodeId: item.json.uploadResult?.episodeId,
  episodeUrl: item.json.uploadResult?.episodeUrl,
  dryRun: item.json.uploadResult?.dryRun || false
}));

const summary = {
  totalProcessed: processedEpisodes.length,
  successful: processedEpisodes.filter(ep => ep.status === 'Success').length,
  failed: processedEpisodes.filter(ep => ep.status === 'Failed').length,
  dryRun: processedEpisodes.some(ep => ep.dryRun),
  episodes: processedEpisodes,
  completedAt: new Date().toISOString()
};

console.log('üéâ Roo Podcast Automation Workflow Complete!');
console.log(`Processed: ${summary.totalProcessed} episodes`);
console.log(`Successful: ${summary.successful}`);
console.log(`Failed: ${summary.failed}`);
if (summary.dryRun) {
  console.log('‚ö†Ô∏è This was a DRY RUN - no actual uploads were made');
}

processedEpisodes.forEach(ep => {
  console.log(`Row ${ep.row}: ${ep.title} (${ep.type}) - ${ep.status}`);
  if (ep.episodeUrl) {
    console.log(`  URL: ${ep.episodeUrl}`);
  }
});

return [{ json: summary }];

// No episodes to process - log and exit gracefully
const result = $json;

console.log('‚ÑπÔ∏è No episodes to process');
console.log('Skipped episodes:', result.skipped);
console.log('The workflow will exit gracefully');

return [{
  json: {
    status: 'completed',
    message: 'No episodes to process',
    skipped: result.skipped,
    timestamp: new Date().toISOString()
  }
}];
